import React from 'react';
import * as ReactBootStrap from 'react-bootstrap';

const ServicecardBmc = () => {
    return (
        <ReactBootStrap.Container fluid>
            <ReactBootStrap.Row style={{
                justifyContent: 'space-between',
                alignItems: 'center',
            }}>
                <ReactBootStrap.Col md={6}>
                    <ReactBootStrap.Card style={{
                        width: 'fit-content',
                        borderTopLeftRadius: 75,
                        borderTopRightRadius: 75,
                        borderBottomLeftRadius: 75,
                        borderBottomRightRadius: 0,
                        padding :'15px 35px',
                        textAlign: 'center',
                        alignItems: 'center',
                        boxShadow:'3px 3px 3px #B1B1B1',
                        letterSpacing:2,
                    }} className="service-logo-card">
                        <img class="service-icon-logo" style={{ width: 195 }} alt="BMC logo" src="/assets/bmc 1.png" />
                        <h3>BMC Remedyforce</h3>

                    </ReactBootStrap.Card>
                </ReactBootStrap.Col>
                <ReactBootStrap.Col md={6}>
                    <h1>Hello</h1>
                    <ReactBootStrap.Button style={{
                                    borderRadius: 20,
                                    padding: '.6rem 1.6rem',
                                    border: '2px solid #fff',
                                    backgroundColor: this.state.btnbg,
                                }} variant="primary" type="submit"
                                    onMouseEnter={() => { this.setState({ btnbg: '#FEB1B1' }) }}
                                    onMouseLeave={() => { this.setState({ btnbg: this.props.btnBg }) }}
                                >
                                    Submit
                            </ReactBootStrap.Button>
                </ReactBootStrap.Col>
            </ReactBootStrap.Row>
        </ReactBootStrap.Container>
    )

}
export default ServicecardBmc;
