import React, { useState } from 'react'
import * as ReactBootStrap from 'react-bootstrap';
import Radium, { Style } from 'radium';


const Myform = (props) => {
    const [Faq, setFaq] = useState({
        name: '',
        number: '',
        email: '',
        question: '',
    });

    const [mystyle, setMyStyle] = useState({
        mystyle: {
            backgroundColor: props.bg,
            color: this.props.text,
            borderRadius: this.props.radius,
            height: this.props.height,
            border: '2px solid #fff',
        }
    });

    return (
        <ReactBootStrap.Container>
            <div style={{
                padding: 25,
            }}>
                <h1 style={{
                    fontSize: '3.5vh'
                }}>Leave your question here</h1>
                <p style={{ color: 'gray' }}>We will try to answer as soon as possible</p>
                <ReactBootStrap.Row>
                    <div style={{
                        marginTop: 15,
                    }}>
                        <Style scopeSelector='myClass' rules={{
                            '::-webkit-input-placeholder': {
                                color: '#fff'
                            },
                            ':-moz-placeholder': {
                                color: '#fff'
                            },
                            '::-moz-placeholder': {
                                color: '#fff'
                            },
                            ':-ms-input-placeholder': {
                                color: '#fff'
                            }
                        }} />

                        <ReactBootStrap.Form>

                            <ReactBootStrap.Form.Row>
                                <ReactBootStrap.Form.Group as={ReactBootStrap.Col}>
                                    <ReactBootStrap.Form.Control className="myClass" style={this.state.mystyle} type="text" placeholder="Your name here" required />
                                </ReactBootStrap.Form.Group>

                                <ReactBootStrap.Form.Group as={ReactBootStrap.Col}>
                                    <ReactBootStrap.Form.Control style={mystyle} type="text" pattern="[0-9]" placeholder="Your number here" maxLength={10} minLength={10} required />
                                </ReactBootStrap.Form.Group>
                            </ReactBootStrap.Form.Row>

                            <ReactBootStrap.Form.Group>
                                <ReactBootStrap.Form.Control style={mystyle} type="email" placeholder="example@example.com" required />
                            </ReactBootStrap.Form.Group>

                            <ReactBootStrap.Form.Group>
                                <ReactBootStrap.Form.Control style={mystyle} placeholder="Your question here" required />
                            </ReactBootStrap.Form.Group>

                            <ReactBootStrap.Button style={{
                                borderRadius: mystyle.borderRadius,
                                padding: '.6rem 1.6rem',
                                border: mystyle.border,
                                backgroundColor: this.props.btnBg,
                            }} variant="primary" type="submit">
                                Submit
                            </ReactBootStrap.Button>
                        </ReactBootStrap.Form>
                    </div>
                </ReactBootStrap.Row>
            </div>
        </ReactBootStrap.Container>
    )
}
export default Myform
