import * as ReactBootStrap from 'react-bootstrap';
import React from 'react';
import Sidebar from 'react-bootstrap-sidebar';

class Side extends React.Component {
    constructor(props) {
        super(props);
        this.state = ({
            isVisible: false,
        })
    }

    updateModal(isVisible) {
        this.setState({
            isVisible: !isVisible,
        })

        this.forceUpdate();
    }


    render() {
        return (
            <div>
                <ReactBootStrap.Button bsStyle="primary" onClick={() => this.updateModal(true)}><span class="glyphicon glyphicon-menu-hamburger"></span></ReactBootStrap.Button>
                <Sidebar side='left' isVisible={this.state.isVisible} onHide={() => this.updateModal(false)}>
                    <ReactBootStrap.Nav>
                        <ReactBootStrap.NavItem href="#">Link 1</ReactBootStrap.NavItem>
                        <ReactBootStrap.NavItem href="#">Link 2</ReactBootStrap.NavItem>
                        <ReactBootStrap.NavItem href="#">Link 3</ReactBootStrap.NavItem>
                        <ReactBootStrap.NavItem href="#">Link 4</ReactBootStrap.NavItem>
                    </ReactBootStrap.Nav>
                </Sidebar>
            </div>
        );
    }
}

export default Side
