import React, { useState } from 'react'
import * as ReactBootStrap from 'react-bootstrap';
import { Spring } from "react-spring/renderprops";
import VisibilitySensor from "react-visibility-sensor";


export default function Services() {
    const [isShown, setisShown] = useState(false);

    const onMouseEnter =()=>{

    }

    return (
        <section id="Services">
            <ReactBootStrap.Container className="s-container">
                <div className="s-header">
                    <h1>Our services</h1>
                    <hr className="underline" />
                </div>
                    <ReactBootStrap.Row className="service-box">


                        <VisibilitySensor>
                            {({ isVisible }) => (
                                <Spring delay={500}
                                    to={{ opacity: isVisible ? 1 : 0 }}
                                >
                                    {({ opacity }) => (
                                        <ReactBootStrap.Col style={{ opacity }} md={5}>
                                            <div className="services-wrapper">
                                                <div onMouseEnter={() => setisShown(true)} onMouseLeave={() => setisShown(false)} className="services-container">
                                                    <h2>Service Management</h2>
                                                    {isShown && (
                                                        <p>
                                                            ITSM,CMDB and Asset Management<br />

                                                HR/Case Management, Facilities Management<br />

                                                Request Management and workflows<br />

                                                Digital Workplace/Mobility<br />

                                                Discovery - Servers and Client Machines<br />
                                                        </p>
                                                    )}

                                                </div>
                                            </div>
                                        </ReactBootStrap.Col>
                                    )}
                                </Spring>
                            )}
                        </VisibilitySensor>

                        <VisibilitySensor>
                            {({ isVisible }) => (
                                <Spring delay={500}
                                    to={{ opacity: isVisible ? 1 : 0 }}
                                >
                                    {({ opacity }) => (
                                        <ReactBootStrap.Col style={{ opacity }} md={5}>
                                            <div className="services-wrapper">
                                                <div onMouseEnter={() => setisShown(true)} onMouseLeave={() => setisShown(false)} className="services-container">
                                                    <h2>Service Management</h2>
                                                    {isShown && (
                                                        <p>
                                                            ITSM,CMDB and Asset Management<br />

                                                HR/Case Management, Facilities Management<br />

                                                Request Management and workflows<br />

                                                Digital Workplace/Mobility<br />

                                                Discovery - Servers and Client Machines<br />
                                                        </p>
                                                    )}

                                                </div>
                                            </div>
                                        </ReactBootStrap.Col>
                                    )}
                                </Spring>
                            )}
                        </VisibilitySensor>

                        <VisibilitySensor>
                            {({ isVisible }) => (
                                <Spring delay={500}
                                    to={{ opacity: isVisible ? 1 : 0 }}
                                >
                                    {({ opacity }) => (
                                        <ReactBootStrap.Col style={{ opacity }} md={5}>
                                            <div className="services-wrapper">
                                                <div onMouseEnter={() => setisShown(true)} onMouseLeave={() => setisShown(false)} className="services-container">
                                                    <h2>Service Management</h2>
                                                    {isShown && (
                                                        <p>
                                                            ITSM,CMDB and Asset Management<br />

                                                HR/Case Management, Facilities Management<br />

                                                Request Management and workflows<br />

                                                Digital Workplace/Mobility<br />

                                                Discovery - Servers and Client Machines<br />
                                                        </p>
                                                    )}

                                                </div>
                                            </div>
                                        </ReactBootStrap.Col>
                                    )}
                                </Spring>
                            )}
                        </VisibilitySensor>

                    </ReactBootStrap.Row>
            </ReactBootStrap.Container>
        </section>
    )
}
