import React from 'react'
import * as ReactBootStrap from 'react-bootstrap';


class Myform extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            mystyle
        }

    }

    render() {
        const myStyle = {
            backgroundColor: this.props.bg,
            color: this.props.text,
            borderRadius: this.props.radius,
            height: this.props.height,
            border: '2px solid #fff',
        }
        return (
            <ReactBootStrap.Container>
                <div style={{
                    padding: 25,
                }}>
                    <h1 style={{
                        fontSize: '3.5vh'
                    }}>Leave your question here</h1>
                    <p style={{ color: 'gray' }}>We will try to answer as soon as possible</p>
                    <ReactBootStrap.Row>
                        <div style={{
                            marginTop: 15,
                        }}>
                            <ReactBootStrap.Form>

                                <ReactBootStrap.Form.Row>
                                    <ReactBootStrap.Form.Group as={ReactBootStrap.Col}>
                                        <ReactBootStrap.Form.Control style={{
                                            myStyle
                                            // backgroundColor: props.bg, 
                                            // color:props.text,
                                            // borderRadius:props.radius,
                                            // height:props.height,
                                            // border:'2px solid #fff'
                                        }} type="text" placeholder="Your name here" />
                                    </ReactBootStrap.Form.Group>

                                    <ReactBootStrap.Form.Group as={ReactBootStrap.Col}>
                                        <ReactBootStrap.Form.Control type="text" pattern="[0-9]" placeholder="Your number here" maxLength={10} minLength={10} required />
                                    </ReactBootStrap.Form.Group>
                                </ReactBootStrap.Form.Row>

                                <ReactBootStrap.Form.Group>
                                    <ReactBootStrap.Form.Control type="email" placeholder="example@example.com" />
                                </ReactBootStrap.Form.Group>

                                <ReactBootStrap.Form.Group>
                                    <ReactBootStrap.Form.Control placeholder="example@example.com" />
                                </ReactBootStrap.Form.Group>



                            </ReactBootStrap.Form>

                        </div>
                    </ReactBootStrap.Row>
                </div>
            </ReactBootStrap.Container>
        )
    }
}
export default Myform
