import React, {StyleSheet} from 'react'
import * as ReactBootStrap from 'react-bootstrap';

const Myform = (props) => {
    return (
        <ReactBootStrap.Container>
            <div style={{
                padding: 25,
            }}>
                <h1 style={{
                    fontSize: '3.5vh'
                }}>Leave your question here</h1>
                <p style={{ color: 'gray' }}>We will try to answer as soon as possible</p>
                <ReactBootStrap.Row>
                    <div style={{
                        marginTop: 15,
                    }}>
                        <ReactBootStrap.Form>

                            <ReactBootStrap.Form.Row>
                                <ReactBootStrap.Form.Group as={ReactBootStrap.Col}>
                                    <ReactBootStrap.Form.Control style={{
                                        backgroundColor:props.bg,
                                        paddingTop: '0.6rem',
                                        padding:'1.5rem'
                                    }} type="text" placeholder="Your name here" />
                                </ReactBootStrap.Form.Group>

                                <ReactBootStrap.Form.Group as={ReactBootStrap.Col}>
                                    <ReactBootStrap.Form.Control type="text" pattern="[0-9]" placeholder="0123456789" maxLength={10} minLength={10} required />
                                </ReactBootStrap.Form.Group>
                            </ReactBootStrap.Form.Row>

                            <ReactBootStrap.Form.Group>
                                <ReactBootStrap.Form.Control type="email" placeholder="example@example.com" />
                            </ReactBootStrap.Form.Group>

                            <ReactBootStrap.Form.Group>
                                <ReactBootStrap.Form.Control placeholder="example@example.com" />
                            </ReactBootStrap.Form.Group>



                        </ReactBootStrap.Form>

                    </div>
                </ReactBootStrap.Row>
            </div>
        </ReactBootStrap.Container>
    )
}


const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: "space-between",
      backgroundColor: "#fff",
      padding: 20,
      margin: 10,
    },
    top: {
      flex: 0.3,
      backgroundColor: "grey",
      borderWidth: 5,
      borderTopLeftRadius: 20,
      borderTopRightRadius: 20,
    },
    middle: {
      flex: 0.3,
      backgroundColor: "beige",
      borderWidth: 5,
    },
    bottom: {
      flex: 0.3,
      backgroundColor: "pink",
      borderWidth: 5,
      borderBottomLeftRadius: 20,
      borderBottomRightRadius: 20,
    },
  });

export default Myform
