import { extend } from 'jquery';
import React from 'react'
import * as ReactBootStrap from 'react-bootstrap';
import FAQsData from '../dataset/FAQs.data';
import { Spring } from "react-spring/renderprops";
import VisibilitySensor from "react-visibility-sensor";


class FAQs extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            FAQsList: FAQsData,
        }
    }

    render() {
        return (
            <section id="Services">
                <ReactBootStrap.Container className="s-container">
                    <div style={{ padding: 50 }} className="s-header">
                        <h1>FAQ<small><small>s</small></small></h1>
                        <hr className="underline" />
                    </div>
                    <ReactBootStrap.Row className="service-box">
                        <ReactBootStrap.Col md={6}>

                            <ReactBootStrap.Accordion defaultActiveKey="0">
                            {this.state.FAQsList.map((item, index) => {
                                return (
                                        <ReactBootStrap.Card>
                                            <ReactBootStrap.Accordion.Toggle as={ReactBootStrap.Card.Header} eventKey={index}>
                                                {item.question} - {index}
                                            </ReactBootStrap.Accordion.Toggle>
                                            <ReactBootStrap.Accordion.Collapse eventKey={index}>
                                                <ReactBootStrap.Card.Body>{item.answer}</ReactBootStrap.Card.Body>
                                            </ReactBootStrap.Accordion.Collapse>
                                        </ReactBootStrap.Card>
                                )
                            })}
                                    </ReactBootStrap.Accordion>
                        </ReactBootStrap.Col>
                        <ReactBootStrap.Col md={6}>

                        </ReactBootStrap.Col>
                    </ReactBootStrap.Row>
                </ReactBootStrap.Container>
            </section>
        )
    }
}
export default FAQs
