import * as ReactBootStrap from 'react-bootstrap';
import FAQsData from '../dataset/FAQs.data';
import JobForm from "./JobForm";

class Footer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            FAQsList: FAQsData,
        }
    }
    render() {
        return (
            <section id="footer" >
                <ReactBootStrap.Container className="footer-container" fluid>
                    <ReactBootStrap.Row style={{ justifyContent: 'space-between', padding: 20, backgroundColor:'rgba(47, 55, 120, 0.6)' }} >
                        <ReactBootStrap.Col md={3}>
                            <h5>About Avatu</h5>
                        </ReactBootStrap.Col>
                        <ReactBootStrap.Col md={3}>
                            col -2
                        </ReactBootStrap.Col>
                        <ReactBootStrap.Col md={3}>
                            col -3
                        </ReactBootStrap.Col>
                        <ReactBootStrap.Col md={3}>
                            col -4 
                        </ReactBootStrap.Col>
                    </ReactBootStrap.Row>
                </ReactBootStrap.Container>
            </section>
        )
    }
}
export default Footer
