import React from 'react'
import * as ReactBootStrap from 'react-bootstrap';
import FAQsData from '../dataset/FAQs.data';
import Myform from "./MessageForm";

class FAQs extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            FAQsList: FAQsData,
        }
    }
    render() {
        return (
            <section id="FAQs">
                <ReactBootStrap.Container className="s-container">
                    <div style={{ padding: 50 }} className="s-header">
                        <h1>FAQ<small><small>s</small></small></h1>
                        <hr className="underline" />
                    </div>
                    <ReactBootStrap.Row className="service-box">
                        <ReactBootStrap.Col md={6}>
                            <div className="FAQs-container">
                                <ReactBootStrap.Accordion style={{
                                    border:'none'
                                }} defaultActiveKey={2}>
                                    {this.state.FAQsList.map((item, index) => {
                                        return (
                                            <ReactBootStrap.Card style={{
                                                border:'none',
                                            }} >
                                                <div style={{ 
                                                    backgroundColor: '#08C2FD',
                                                    borderRadius:5,
                                                    margin:4,
                                                    zIndex:2,
                                                    }}>
                                                    <ReactBootStrap.Accordion.Toggle
                                                    style={{
                                                        color:'#fff',
                                                        fontSize:18,
                                                    }}
                                                     as={ReactBootStrap.Card.Header} eventKey={index}>
                                                        {item.question}
                                                    </ReactBootStrap.Accordion.Toggle>
                                                </div>
                                                <div style={{ 
                                                    zIndex:1,
                                                    backgroundColor: '#eee',
                                                    margin:4,
                                                    marginTop:-7,
                                                    borderBottomLeftRadius:10,
                                                    borderBottomRightRadius:10
                                                    }}>
                                                    <ReactBootStrap.Accordion.Collapse eventKey={index}>
                                                        <ReactBootStrap.Card.Body
                                                            bg="danger"
                                                        >{item.answer}<br/><br/>
                                                        <strong>Asked by : </strong><p style={{
                                                            color:'rgb(252, 165, 165)'
                                                        }}><i>{item.askedby}</i> {item.date}</p>
                                                        </ReactBootStrap.Card.Body>
                                                    </ReactBootStrap.Accordion.Collapse>
                                                </div>
                                            </ReactBootStrap.Card>
                                        )
                                    })}
                                </ReactBootStrap.Accordion>
                            </div>
                        </ReactBootStrap.Col>
                        <ReactBootStrap.Col md={6}>

                            <Myform  name="hello"/>

                        </ReactBootStrap.Col>
                    </ReactBootStrap.Row>
                </ReactBootStrap.Container>
            </section>
        )
    }
}
export default FAQs
